:root {
  --radius: 0.75rem;

  /* Base colors ‚Äî warm off-white */
  --background: oklch(0.975 0.004 264);
  --foreground: oklch(0.18 0.02 264);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.02 264);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.02 264);

  /* Primary ‚Äî deep indigo */
  --primary: oklch(0.42 0.19 264);
  --primary-foreground: oklch(0.98 0 0);

  /* Secondary / Muted */
  --secondary: oklch(0.94 0.006 264);
  --secondary-foreground: oklch(0.42 0.19 264);
  --muted: oklch(0.95 0.004 264);
  --muted-foreground: oklch(0.52 0.01 264);
  --accent: oklch(0.93 0.012 264);
  --accent-foreground: oklch(0.30 0.12 264);

  /* System */
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.91 0.006 264);
  --input: oklch(0.91 0.006 264);
  --ring: oklch(0.42 0.19 264);

  /* Charts */
  --chart-1: oklch(0.42 0.19 264);
  --chart-2: oklch(0.60 0.16 200);
  --chart-3: oklch(0.72 0.18 85);
  --chart-4: oklch(0.55 0.20 30);
  --chart-5: oklch(0.65 0.17 320);

  /* Sidebar ‚Äî deep indigo */
  --sidebar: oklch(0.26 0.09 264);
  --sidebar-foreground: oklch(0.92 0.008 264);
  --sidebar-primary: oklch(0.58 0.20 264);
  --sidebar-primary-foreground: oklch(0.98 0 0);
  --sidebar-accent: oklch(0.32 0.09 264);
  --sidebar-accent-foreground: oklch(0.92 0.008 264);
  --sidebar-border: oklch(0.33 0.07 264);
  --sidebar-ring: oklch(0.58 0.20 264);

  /* Custom tokens */
  --gradient-hero: linear-gradient(135deg, oklch(0.42 0.19 264), oklch(0.52 0.22 285));
  --gradient-accent: linear-gradient(135deg, oklch(0.60 0.16 200), oklch(0.65 0.17 220));
  --gradient-warm: linear-gradient(135deg, oklch(0.72 0.18 85), oklch(0.68 0.20 60));
  --gradient-subtle: linear-gradient(160deg, oklch(0.975 0.004 264), oklch(0.95 0.010 264));
  --shadow-card: 0 2px 8px oklch(0.18 0.02 264 / 0.06), 0 0 1px oklch(0.18 0.02 264 / 0.08);
  --shadow-hero: 0 12px 40px -8px oklch(0.42 0.19 264 / 0.35);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

