@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700;800&display=swap');
@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
  --font-sans: 'Plus Jakarta Sans', sans-serif;
}

:root {
  --radius: 0.75rem;

  /* Warm cream base */
  --background: oklch(0.978 0.006 80);
  --foreground: oklch(0.16 0.02 255);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.16 0.02 255);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.16 0.02 255);

  /* Deep indigo primary */
  --primary: oklch(0.38 0.16 264);
  --primary-foreground: oklch(0.985 0 0);
  --secondary: oklch(0.95 0.01 264);
  --secondary-foreground: oklch(0.38 0.16 264);
  --muted: oklch(0.96 0.005 80);
  --muted-foreground: oklch(0.52 0.03 255);
  --accent: oklch(0.94 0.02 264);
  --accent-foreground: oklch(0.38 0.16 264);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.91 0.01 264);
  --input: oklch(0.93 0.01 264);
  --ring: oklch(0.38 0.16 264 / 0.4);

  /* Charts */
  --chart-1: oklch(0.52 0.18 264);
  --chart-2: oklch(0.62 0.14 180);
  --chart-3: oklch(0.72 0.16 60);
  --chart-4: oklch(0.65 0.18 310);
  --chart-5: oklch(0.58 0.19 30);

  /* Sidebar â€” deep indigo */
  --sidebar: oklch(0.22 0.08 264);
  --sidebar-foreground: oklch(0.94 0.02 264);
  --sidebar-primary: oklch(0.62 0.18 264);
  --sidebar-primary-foreground: oklch(1 0 0);
  --sidebar-accent: oklch(0.30 0.10 264);
  --sidebar-accent-foreground: oklch(0.94 0.02 264);
  --sidebar-border: oklch(0.30 0.08 264);
  --sidebar-ring: oklch(0.62 0.18 264);

  /* Custom design tokens */
  --gradient-hero: linear-gradient(135deg, oklch(0.38 0.16 264), oklch(0.48 0.20 285));
  --gradient-subtle: linear-gradient(160deg, oklch(1 0 0), oklch(0.96 0.015 264));
  --gradient-card-glow: linear-gradient(135deg, oklch(0.96 0.02 264), oklch(0.98 0.01 80));
  --shadow-card: 0 2px 12px -2px oklch(0.38 0.16 264 / 0.1), 0 1px 3px -1px oklch(0 0 0 / 0.06);
  --shadow-hero: 0 20px 60px -15px oklch(0.38 0.16 264 / 0.35);
  --shadow-elevated: 0 8px 32px -8px oklch(0.38 0.16 264 / 0.2);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'Plus Jakarta Sans', sans-serif;
    -webkit-font-smoothing: antialiased;
  }
  h1, h2, h3 {
    font-family: 'Plus Jakarta Sans', sans-serif;
    letter-spacing: -0.02em;
  }
}

@layer utilities {
  .hero-gradient {
    background: var(--gradient-hero);
  }
  .card-shadow {
    box-shadow: var(--shadow-card);
  }
  .elevated-shadow {
    box-shadow: var(--shadow-elevated);
  }
  .hero-shadow {
    box-shadow: var(--shadow-hero);
  }
  .card-glow {
    background: var(--gradient-card-glow);
  }
  .transition-smooth {
    transition: var(--transition-smooth);
  }
}

